mapper.update:
update item 


		ITEM_NAME=#{itemName}


		ITEM_QTY=#{itemQty}


		ITEM_PPRICE=#{itemPprice}


		ITEM_SPRICE=#{itemSprice}


		ITEM_VENDOR=#{itemVendor} 


where ITEM_NUM=#{itemNum}

update item set ITEM_DM=#{itemDm},


		MIN_QTY=#{minQty}


		ITEM_MARGIN=#{itemMargin}


where ITEM_NUM=#{itemNum}

mapper.select:
select * from item where min_qty!=0


		ITEM_NUM LIKE '%'||#{itemNum}||'%' 


		ITEM_NAME=#{itemName} 


		ITEM_VENDOR=#{itemVendor} 


		ITEM_DM=#{itemDm} 


order by ITEM_NUM asc
